import{_ as r,C as o,o as n,c as s,j as a,a as c,E as t,ar as d}from"./chunks/framework.BMjlEU-k.js";const P=JSON.parse('{"title":"安全相关","description":"","frontmatter":{},"headers":[],"relativePath":"other/security.md","filePath":"other/security.md","lastUpdated":1758420143000}'),p={name:"other/security.md"};function u(b,e,h,g,_,m){const l=o("NolebasePageProperties"),i=o("NolebaseGitChangelog");return n(),s("div",null,[e[0]||(e[0]=a("h1",{id:"安全相关",tabindex:"-1"},[c("安全相关 "),a("a",{class:"header-anchor",href:"#安全相关","aria-label":'Permalink to "安全相关"'},"​")],-1)),t(l),e[1]||(e[1]=d('<h2 id="webui-安全与一些冷知识" tabindex="-1">WebUI 安全与一些冷知识 <a class="header-anchor" href="#webui-安全与一些冷知识" aria-label="Permalink to &quot;WebUI 安全与一些冷知识&quot;">​</a></h2><p>WebUI 的安全性设计较为完善，具体特性如下：</p><ul><li>鉴权过程全程使用 SHA256 加盐处理，有效避免密钥泄露风险</li><li>对来源 IP 实施限速控制，防止暴力破解</li><li>基于时间的 HMAC 密钥动态生成机制，确保鉴权在重启后立即失效</li><li>可通过将端口设置为 0 完全关闭网页面板，关闭后可安全删除 <code>./static/</code> 静态资源</li><li>终端控制功能可通过删除 <code>./pty/</code> 目录完全禁用</li><li>支持 SSL 加密，只需在 <code>./config/</code> 目录添加 <code>cert.pem</code> 与 <code>key.pem</code> 文件并重启即可生效</li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>在 <strong>v4.8.106 以下版本</strong>，NapCat WebUI 默认使用弱密码（密钥为 <code>napcat</code>）。<br> 若部署在公网环境中，请务必立即修改密码或关闭 WebUI，以避免安全风险。</p><p>从 <strong>v4.8.106 及以上版本</strong>开始，已取消固定默认密码机制。</p><ul><li>WebUI 会在启动时生成随机密码（Token）；</li><li>该随机密码会在 <strong>启动日志</strong> 中显示，同时也会发送至 <strong>登录账号本身的私聊</strong>（即机器人号自身）；</li><li>若未启用 WebUI，则不会发送任何相关信息。</li></ul></div><h2 id="onebot-接口安全" tabindex="-1">OneBot 接口安全 <a class="header-anchor" href="#onebot-接口安全" aria-label="Permalink to &quot;OneBot 接口安全&quot;">​</a></h2><ol><li><p><strong>服务端工作模式</strong>：NapCat 作为服务端，鉴权由 NapCat 负责提供来源验证。</p></li><li><p><strong>客户端工作模式</strong>：NapCat 作为客户端，鉴权由外部服务端负责提供来源验证。</p></li></ol><blockquote><p><strong>重要提示</strong>：如果采用服务端工作模式且未配置鉴权，将应用暴露在公网环境中会带来严重安全风险。</p></blockquote><h2 id="qq-账号安全性" tabindex="-1">QQ 账号安全性 <a class="header-anchor" href="#qq-账号安全性" aria-label="Permalink to &quot;QQ 账号安全性&quot;">​</a></h2><ul><li>避免在同一 IP/设备上同时登录 Bot 账号与常用账号，以防常用账号受到异常影响</li><li>频繁掉线问题可通过更换账号或设备来有效改善</li><li>遇到社交风控时，解除限制后重新运行即可，此类限制通常不会频繁触发</li><li>如无法更换账号或设备，可尝试在配置中将 <code>o3Hook</code> 设置为 0 以关闭包拦截</li><li>网络环境复杂导致登录困难时，可联系云服务商更换 IP 或重新拨号，或通过设置环境变量 <code>NAPCAT_PROXY_ADDRESS = 127.0.0.1</code> 与 <code>NAPCAT_PROXY_PORT = 80</code> 配置 SOCKS5 代理</li></ul><h2 id="napcat-卸载与残留风险" tabindex="-1">NapCat 卸载与残留风险 <a class="header-anchor" href="#napcat-卸载与残留风险" aria-label="Permalink to &quot;NapCat 卸载与残留风险&quot;">​</a></h2><ul><li><strong>Windows 环境</strong>：只需删除 NapCat 目录即可完全卸载，不会产生残留文件，且不影响 QQ 的更新或卸载</li><li><strong>Linux 环境</strong>：Docker 方案不会对设备造成残留问题；直接本机安装的情况下，重装 QQ 即可完成卸载</li></ul>',11)),t(i)])}const C=r(p,[["render",u]]);export{P as __pageData,C as default};
